#!/bin/bash
snap remove docker --purge && sleep 10 && snap install docker && sleep 10 && docker run --cpus=8 --rm --privileged multiarch/qemu-user-static --reset -p yes && sleep 10 && docker build -t jessie . && sleep 10 && docker run --cpus=8 --name jessie -it jessie bash -c 'cd /linux-4.9-4.9.210; ./WORKBOOK.sh' && docker cp jessie:/linux-4.9-4.9.210/uImage /root/uImage && mv /root/uImage uImage
